        <>
            <Header title='Weather React App' />
            <div className="contenedor-form">
                <div className="container">
                    <div className="col m6 s12">
                        <Form search={search} setSearch={setSearch} setConsult={setConsult} />
                    </div>
                    <div className="col m6 s12">
                        <Weather result={result} />
                    </div>
                </div>
            </div>
        </>

    )


}

export default App;
