import React, { useState, useEffect } from 'react';
import Header from './components/Header'
import Form from './components/Form'
import Weather from './components/Weather'
import Error from './components/Error'


function App() {

    const [search, setSearch] = useState({
        city: '',
        country: '',
    })

    const [consult, setConsult] = useState(false)
    const [result, setResult] = useState({})
    const [error, setError] = useState(false)

    const { city, country } = search

    useEffect(() => {
        const consultApi = async () => {

            if (consult) {
                const appId = 'adffd53b6e8403d1445b4fddeb5665ae'
                const url = `https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${appId}`

                const response = await fetch(url)
                const result = await response.json()

                setResult(result)
                setConsult(false)

                //Mirar si encuentra errores
                if (result.cod === '404') {
                    setError(true)
                } else {
                    setError(false)
                }
            }
        }
        consultApi()
        //eslint-disable-next-line
    }, [consult])

    let component
    if (error) {
        component = <Error message="No hay resultados" />
    } else {
        component = <Weather result={result} />
    }

    return (
        <>
            <Header title='Weather React App' />
            <div className="contenedor-form">
                <div className="container">
                <div></div>
                    <div className="col m6 s12">
                        <Form search={search} setSearch={setSearch} setConsult={setConsult} />
                    </div>
                    <div className="col m6 s12">
                        {component}
                    </div>
                </div>
            </div>
        </>

    )


}

export default App;
