import React, { useState } from 'react'

const Form = () => {

    const [search, setSearch] = useState({
        city: '',
        country: '',
    })

    const [error, setError ] = useState(false)
    const { city, country } = search

    const handleChange = e => {
        // actualizar el state
        setSearch({
            ...search,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = e => {
        e.preventDefault()
        //validar
        if(city.trim() === '' || country.trim() === ''){
            setError(true)
            return 
        }

    }

    return (
        <form onSubmit={handleSubmit}>
            {error ? <p className="red darken-4">Todos los campos son obligatorios</p> : null}
            <div className="input-field col s12">
                <input type="text" name="city" id="city" value={city} onChange={handleChange} />
                <label htmlFor="city">Ciudad:</label>
            </div>
            <div className="input-field col s12">
                <select name="country" id="country" value={country} onChange={handleChange}>
                    <option value="">-- Selecciona un país --</option>
                    <option value="US">Estados Unidos</option>
                    <option value="MX">México</option>
                    <option value="AR">Argentina</option>
                    <option value="CO">Colombia</option>
                    <option value="CR">Costa Rica</option>
                    <option value="ES">España</option>
                    <option value="PE">Perú</option>
                </select>
                <label htmlFor="country">País:</label>
            </div>
            <div className="input-field col s12">
                <input type="submit" value="Buscar Clima" />
            </div>

        </form>
    );
}

export default Form;
